mapper.select:
select * from ( select ROW_NUMBER() over (order by goods_num desc) as rn, goods_Num goodsNum, goods_name goodsName, goods_price goodsPrice ,goods_content goodsContent, delivery_cost deliveryCost, emp_num empNum ,goods_regist goodsRegist, goods_main_store goodsMainStore ,goods_main_store_img goodsMainStoreImg, goods_images goodsImages ,goods_images_img goodsImagesImg, visit_count visitCount ,update_Emp_Num updateEmpNum, goods_Update_Date goodsUpdateDate from goods
goods_name like '%' || #{searchWord} || '%' or goods_num like '%' || #{searchWord} || '%'
)sub1 where rn between #{startRow} and #{endRow}

mapper.delete:
delete from goods 
where goods_num in 
						#{goodsNum}

mapper.update:
 
update goods
set GOODS_NAME = #{goodsName}, GOODS_PRICE = #{goodsPrice}
	,delivery_cost =#{deliveryCost}, GOODS_CONTENT = #{goodsContent}
    ,update_emp_num = #{updateEmpNum}, goods_update_date = now()
	
	,goods_main_store = #{goodsMainStore}
	,goods_main_store_img = #{goodsMainStoreImg}
	
	
	,goods_images = #{goodsImages}
	,goods_images_img = #{goodsImagesImg}	
	
where goods_num = #{goodsNum}
